async function KopfzeileAktualisieren() {
  // Das Header-Element
  var Kopfzeile  = document.getElementById("Kopfzeile");
  var NeueBreite = Kopfzeile.offsetWidth;
  var NeueHöhe   = Kopfzeile.offsetHeight;
  var Breite     = NeueBreite;
  var Höhe       = NeueHöhe;

  // Die Kompass-SVG-Definition und die Umrahmung
  var SVGKompass    = document.getElementById("Kopfzeilenkompass"); // width, heigth und viewbox anpassen
  var SVGUmrahmung  = document.getElementById("kzkUmrahmung");      // d anpassen
  var SVGUmrahmungF = document.getElementById("kzkUmrahmungF");     // d anpassen

  while (true) {
    await Warte(1000);

    var NeueBreite = Kopfzeile.offsetWidth;
    var NeueHöhe   = Kopfzeile.offsetHeight;
    if (NeueBreite == Breite && NeueHöhe == Höhe)
      continue;

    Breite = NeueBreite;
    Höhe   = NeueHöhe;
    SVGKompass.setAttribute("viewBox", "-55 -49 " + Breite + " " + Höhe);
    SVGUmrahmung.setAttribute("d", "M 25.5 -44.16729559 Q 27.7128 -48 30.7128 -48 L " + (Breite-58) + " -48 Q " + (Breite-56) + " -48 " + (Breite-56) + " -45 L " + (Breite-56) + " " + (Höhe-53) + " Q " + (Breite-56) + " " + (Höhe-50) + " " + (Breite-58) + " " + (Höhe-50) + " L -51 " + (Höhe-50) + " Q -54 " + (Höhe-50) + " -54 " + (Höhe-53) + " L -54 3 Q -54 0 -51 0");
    SVGUmrahmungF.setAttribute("d", "M 25.5 -44.16729559 Q 27.7128 -48 30.7128 -48 L " + (Breite-58) + " -48 Q " + (Breite-56) + " -48 " + (Breite-56) + " -45 L " + (Breite-56) + " " + (Höhe-53) + " Q " + (Breite-56) + " " + (Höhe-50) + " " + (Breite-58) + " " + (Höhe-50) + " L -51 " + (Höhe-50) + " Q -54 " + (Höhe-50) + " -54 " + (Höhe-53) + " L -54 3 Q -54 0 -51 0");
  }
}



