svg.script:
    // Bildschirmabmessungen ermitteln
    var Bildschirmbreite = screen.width;
    var Bildschirmhöhe   = screen.height;

    var Fensterbreite = window.innerWidth;
    var Fensterhöhe = window.innerHeight;

    var Spaltenanzahl = Math.ceil(Fensterbreite / 20);
    var Zeilenanzahl  = Math.ceil(Fensterhöhe / 20);

    var Auflösung = window.devicePixelRatio;

    var MeinText = document.getElementById('MeinText');
    MeinText.textContent = Auflösung + " " + Bildschirmbreite + " " + Bildschirmhöhe + " " + Fensterbreite + " " + Fensterhöhe;


    // Funktion, um die Linie ein- und auszublenden
    function toggleBlink() {
      var line = document.getElementById("blinkLine");
      line.style.display = (line.style.display == "none") ? "inherit" : "none";
    }

    // Funktion, um die Linie zu blinken
    function blinkLine() {
      setInterval(toggleBlink, Math.floor(Math.random() * 1000) + 500); // Zufällige Zeit zwischen 500ms und 2500ms
    }

    // Starte das Blinken, sobald das Dokument geladen ist
    window.onload = blinkLine;
